import React, {Component} from 'react';

class Section extends Component{
    render() {
        return (
            <section id="about"><div class="container"><div class="row"><div class="col-lg-6 col-lg-offset-3 text-center"><h2 class="section-heading">Double Your Chance of Getting Hired in 45 Minutes</h2></div></div></div><div class="container content"><div class="row"><div class="col-lg-4 col-md-6 text-center"><div class="service-box"><i class="fa fa-4x fa-group fa-icon-1"></i><h3>Get introduced to interviewers</h3><p>Tell us about your target companies, preferred time slots, etc.
We'll introduce you to experienced interviewers who can help you.</p></div></div><div class="col-lg-4 col-md-6 text-center"><div class="service-box"><i class="fa fa-4x fa-desktop fa-icon-2"></i><h3>Conduct interview online</h3><p>Interviews are conducted via Skype with the help of code-sharing
tools.</p></div></div><div class="col-lg-4 col-md-6 text-center"><div class="service-box"><i class="fa fa-4x fa-heart fa-icon-3"></i><h3>Get real feedback</h3><p>Get immediate feedback at the end of the interview. You are also
encouraged to ask whatever questions you have.</p></div></div></div></div></section>
        )

    }

}

export default Section
